const currentHangmanString = readline()
printErr(`currentHangmanString : ${currentHangmanString}`)
const N = parseInt(readline())
for (let i = 0; i < N; i++) {
    const word = readline()
    printErr(`Process word : ${word}`)
    if (word.length == currentHangmanString.length){
        let isWordValid = currentHangmanString.split``.every((c,i) => {
            if (c == "_") {
                return !currentHangmanString.includes(word.split``[i])
            }
            else {
                return c == word.split``[i]
            }
        })
        isWordValid ? print(word) : null
    }
}
