package main

import (
    "fmt"
    "strconv"
)

func main() {
    // 1. Read input as a string (matches the Stub Generator 'word' type)
    var RStr string
    fmt.Scan(&RStr)

    // 2. Parse the string into an int64 manually
    // This bypasses any editor input limits since we read text, not a number directly.
    R, _ := strconv.ParseInt(RStr, 10, 64)

    // Binary Search Solution
    // Low is 0, High is 80,000,000 (Updated safe constraints)
    var low int64 = 0
    var high int64 = 80000000
    var ans int64 = -1

    for low <= high {
        mid := (low + high) / 2

        // Calculate Magic Level for mid
        // Formula: F^2 + F * floor(F/7)
        magicLevel := (mid * mid) + (mid * (mid / 7))

        if magicLevel == R {
            ans = mid
            break
        } else if magicLevel < R {
            low = mid + 1
        } else {
            high = mid - 1
        }
    }

    fmt.Println(ans)
}
