from itertools import product

(qc, qr), (kc, kr) = input().split()
q_pos = {(qc, qr)}
for dr, dc in product(range(-1, 2), repeat=2):
    if dr == dc == 0:
        continue
    k = 1
    while ord("a") <= ord(qc) + dc * k <= ord("h") and 1 <= int(qr) + dr * k <= 8:
        q_pos.add((chr(ord(qc) + dc * k), str(int(qr) + dr * k)))
        k += 1
if (kc, kr) in q_pos:
    print(f"Queen sees knight on {kc}{kr}")
    exit()

cc, cr = kc, kr
for _ in range(int(input())):
    nc, nr = input()
    if {abs(ord(nc) - ord(cc)), abs(int(nr) - int(cr))} != {1, 2}:
        print(f"Knight attempting invalid move from {cc}{cr} to {nc}{nr}")
        break
    elif (nc, nr) in q_pos:
        print(f"Queen sees knight on {nc}{nr}")
        break
    cc, cr = nc, nr
else:
    print(f"Knight successfully manoeuvred from {kc}{kr} to {cc}{cr}")
