const chordsCount = parseInt(readline());

let sum = 0;

for (let i = 0; i < chordsCount; i++) {
    const chord = readline().split('-');
    chord.forEach((note, index) => {
        if (note !== 'X') {
            sum +=((index + 1) + (index * 21)) + Number(note);
        }
    })}

console.log(sum);
