def is_balanced(num_str):
    length = len(num_str)

    if length % 2 == 0:
        # Nombre à longueur paire, diviser en 2 moitiés égales
        left = num_str[:length // 2]
        right = num_str[length // 2:]
    else:
        # Nombre à longueur impaire, ignorer le chiffre du milieu
        mid = length // 2
        left = num_str[:mid]
        right = num_str[mid+1:]

    sum_left = sum(int(d) for d in left)
    sum_right = sum(int(d) for d in right)

    return sum_left == sum_right

def main():
    n = int(input())
    balanced_numbers = []

    for _ in range(n):
        x = input().strip()
        if is_balanced(x):
            balanced_numbers.append(x)

    print(len(balanced_numbers))
    for number in balanced_numbers:
        print(number)

if __name__ == "__main__":
    main()
