use std::io;

macro_rules! parse_input {
    ($x:expr, $t:ident) => ($x.trim().parse::<$t>().unwrap())
}

fn main() {
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let inputs = input_line.split(" ").collect::<Vec<_>>();
    let width = parse_input!(inputs[0], i32);
    let height = parse_input!(inputs[1], i32);

    let mut grid = Vec::new();
    for i in 0..height as usize {
        let mut inputs = String::new();
        io::stdin().read_line(&mut inputs).unwrap();
        let mut row = Vec::new();
        for j in inputs.split_whitespace() {
            let cost = parse_input!(j, i32);
            row.push(cost);
        }
        grid.push(row);
    }

    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let path = input_line.trim_matches('\n').to_string();

    let mut total_cost = 0;
    for coord in path.split_whitespace() {
        let parts: Vec<&str> = coord.split(',').collect();
        let x: usize = parts[0].parse().unwrap();
        let y: usize = parts[1].parse().unwrap();

        total_cost += grid[y][x];
    }

    println!("{}", total_cost);
}
