var target = parseInt(readline());
var n = parseInt(readline());

/*
target =  3662
n = 71
*/
let improved
let memory = []
let bestCount = 12
let max = 30

let obtained = []
for (let i = 0; i < max ; i++){
    obtained[i] = []
}
let count

obtained[1].push(n)
memory[n] = { 'count' : 0, 'op' : 0 }

// ------------------------------------------------------------------------------------------------------------
function
check(value) {
    if (value===target && memory[value].count<bestCount ) {
        bestCount = memory[value].count
    }
}
// ------------------------------------------------------------------------------------------------------------
function
operation(value, a, op , b){   // value = a '+/*-' b
    let newCount = memory[a].count + memory[b].count + 1
    if (typeof memory[value] === "undefined" || newCount < memory[value].count) { // better way to get to value
        memory[value] = { 
            'count' : newCount, 
            'a'     : a, 
            'b'     : b, 
            'op'    : op
        }
        obtained[count].push(value)
        improved++;
        check(value)
    }
}
// ------------------------------------------------------------------------------------------------------------
function
operations(a,b){ // assert(a>=b)
    if ( a < b ) return operations( b, a ) // swap

    if ( a !== b )      operation( a - b, a, '-', b)
    if ( a % b === 0 )  operation( a / b, a, '/', b)
    operation( a + b, a, '+', b)
    operation( a * b, a, '*', b)
}
// ------------------------------------------------------------------------------------------------------------
function
loop(i,j) {
    for (let aa in obtained[i]) {
        let a = obtained[i][aa]
        for (let bb in obtained[j]) {
            let b = obtained[j][bb]
            if ( memory[a].count + memory[b].count >= bestCount) continue;
            operations( a, b )
            operations( b, a )
        }
    }
}
// ------------------------------------------------------------------------------------------------------------

for (count = 2; count < max; count++) {
    improved = 0
    for (let i = 1;; i++) {
        let j = count - i
        if (j < i) break;
        loop(i, j )
    }
    if (improved==0) break;
}
print(bestCount+1)
