import sys

def digital_root(n):
    if n == 0:
        return 0
    return n % 9 if n % 9 != 0 else 9

def solve():
    N = int(sys.stdin.readline().strip())
    current = digital_root(N)

    # Pattern detection based on digital root cycles
    if current in [1, 2, 4, 5, 7, 8]:
        print(6)
    elif current in [3, 6]:
        print(2)
    else:  # current == 9
        print(1)

if __name__ == "__main__":
    solve()
