use std::io;

fn main() {
    let mut input = String::new();
    io::stdin().read_line(&mut input).unwrap();
    let weight_kg: f64 = input.trim().parse().unwrap();

    input.clear();
    io::stdin().read_line(&mut input).unwrap();
    let dose_mg: f64 = input.trim().parse().unwrap();

    input.clear();
    io::stdin().read_line(&mut input).unwrap();
    let conc: f64 = input.trim().parse().unwrap();

    println!("{:.1}", (weight_kg * dose_mg) / conc);
}
