use std::io;

fn main() {
    let mut input = String::new();
    let read = |s: &mut String| { s.clear(); io::stdin().read_line(s).unwrap(); s.trim().parse::<f64>().unwrap() };
    let mut s = String::new();
    let weight = read(&mut s);
    let mut dose = read(&mut s);
    let conc = read(&mut s);
    let threshold = read(&mut s);
    let min_vol = read(&mut s);
    let max_vol = read(&mut s);

    if weight < threshold { dose /= 2.0; }
    let volume = (weight * dose) / conc;

    if volume > max_vol { println!("OVERDOSE"); }
    else if volume < min_vol { println!("UNDERDOSE"); }
    else { println!("{:.1}", volume); }
}
