var dict = {};

var N1 = parseInt(readline());
for (var i = 0; i < N1; i++) {
    var W1 = readline().split(";");
    dict[W1[0]] = {"first": W1[1], "second": ""};
}
var N2 = parseInt(readline());
for (var i = 0; i < N2; i++) {
    var W2 = readline().split(";");
    if (W2[0] in dict) {
        dict[W2[0]].second = W2[1];
    } else {
        dict[W2[0]] = {"first": W2[1], "second": ""};
    }
}

var keys = Object.keys(dict);
for (var i in keys) {
    print(keys[i] + ": " + dict[keys[i]].first + ((dict[keys[i]].second !== "") ? (" and " + dict[keys[i]].second) : ("")));
}
