import string

a = int(input())
b = int(input())
msg = input().upper()

if a%2==0 or a%13==0 or a%26==0:
    print("error")
else:
    l = string.ascii_uppercase
    c=""

    for i in range(26):
        c+=l[(a*i + b)%26]
    r=""
    for x in msg:
        if x.isalpha():
            r+=l[c.index(x)]
        else:
            r+=x
    print(r)


