import java.util.*;
import java.util.stream.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse the standard input
 * according to the problem statement.
 **/
class Solution {

	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();
		ArrayList<Hand> hands = new ArrayList<>(N);
		for (int i = 0; i < N; i++) {
			String name = in.next();
			int X = in.nextInt();
			int Y = in.nextInt();
			int Z = in.nextInt();
			hands.add(new Hand(name, X, Y, Z));
		}

		Collections.sort(hands);

		int last = 1;
		while (true) {
			if (hands.get(0).compareTo(hands.get(last)) == 0) ++last;
			else break;
		}

		System.out.println(hands.stream().limit(last).map(h -> h.player).sorted().collect(Collectors.joining(" ")));
	}

	public static class Hand implements Comparable<Hand> {
		public final String player;
		public final Integer[] cards;
		public final Integer value;

		public Hand(String name, int x, int y, int z) {
			player = name;
			cards = new Integer[3];
			cards[0] = Integer.min(Integer.min(x, y), z);
			cards[2] = Integer.max(Integer.max(x, y), z);
			cards[1] = x + y + z - cards[0] - cards[2];
			int thisV = -1;
			if (triple()) thisV = 3;
			else if (straight()) thisV = 2;
			else if (pair()) thisV = 1;
			else thisV = 0;
			value = thisV;
		}

		private boolean straight() {
			return cards[0] + 1 == cards[1] && cards[1] + 1 == cards[2];
		}

		private boolean triple() {
			return cards[0] == cards[1] && cards[1] == cards[2];
		}

		private boolean pair() {
			return cards[0] == cards[1] || cards[1] == cards[2];
		}

		public int compareTo(Hand that) {
			if (this.value != that.value) return that.value.compareTo(this.value);
			switch (this.value) {
			case 3: // triple
				return that.cards[0].compareTo(this.cards[0]);
			case 2: // straight
				return that.cards[2].compareTo(this.cards[2]);
			case 0: // high card
				int comp = that.cards[2].compareTo(this.cards[2]);
				if (comp != 0) return comp;
				comp = that.cards[1].compareTo(this.cards[1]);
				if (comp != 0) return comp;
				else return that.cards[0].compareTo(this.cards[0]);
			default: // pair
				Integer thisPair, thisExtra, thatPair, thatExtra;

				if (this.cards[0] == this.cards[1]) {
					thisPair = this.cards[0];
					thisExtra = this.cards[2];
				} else {
					thisPair = this.cards[1];
					thisExtra = this.cards[0];
				}

				if (that.cards[0] == that.cards[1]) {
					thatPair = that.cards[0];
					thatExtra = that.cards[2];
				} else {
					thatPair = that.cards[1];
					thatExtra = that.cards[0];
				}
				if (thisPair != thatPair) return thatPair.compareTo(thisPair);
				else return thatExtra.compareTo(thisExtra);
			}
		}
	}
}

