import java.util.HashSet;
import java.util.LinkedList;
import java.util.Scanner;
import java.util.stream.Collectors;
import java.util.stream.Stream;

class Solution {

	public static void main(String args[]) {
		try (Scanner in = new Scanner(System.in)) {
			int n = in.nextInt();
			System.out.println(rec().limit(n+1).collect(Collectors.toCollection(LinkedList::new)).getLast());
		}
	}

	public static Stream<Integer> rec() {
		class Rec {
			HashSet<Integer> set = new HashSet<>();
			{set.add(0);}
			int jump = 1;
			int value = 0;
			Rec next() {
				int diff = value-jump;
				if (diff < 0 || set.contains(diff)) value+=jump;
				else value-=jump;
				++jump;
				set.add(value);
				return this;
			}
		}
		return Stream.iterate(new Rec(), Rec::next).map(r->r.value);
	}

}
