import java.util.*
import java.io.*
import java.math.*

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
fun main(args : Array<String>) {
    val input = Scanner(System.`in`)
    val M = input.nextInt()
    val N = input.nextInt()

    var player = 0
    val score = Array(2){0}
    var pile = M

    for (i in 0 until N) {
        val x = input.nextInt()

        player = 1-player // denotes the player who gets the win
        pile -= x

        if(pile < 0){
            // For debugging the testcases
            println("NEGATIVE $i")
            return 
        }
        if(pile % 3 == 0 || pile % 5 == 0){
            score[player] ++
            pile = M
        }
    }

    println("${score[0]} ${score[1]}")
}
