import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        int I = 0;
        int J = 0;
        int K = 0;

        for (int i = 0; i < N; i++) {
            String vector = in.nextLine();
            vector = vector.replaceAll(" ", "");
            String[] comp = vector.split("[a-zA-Z]");
            I += Integer.valueOf(comp[0]);
            J += Integer.valueOf(comp[1]);
            K += Integer.valueOf(comp[2]);

        }

        // Write an answer using System.out.println()
        // To debug: System.err.println("Debug messages...");

        System.out.println(I+"i "+J+"j "+K+"k");
        System.out.println((int)Math.sqrt(Math.pow(I,2) + Math.pow(J,2) + Math.pow(K,2)));
    }
}
