#include <iostream>

using namespace std;

int main()
{
    int n;
    cin >> n; cin.ignore();

    int o[256] = {0};

    o['i'] = 1;
    o['z'] = 2;
    o['e'] = 3;
    o['a'] = 4;
    o['s'] = 5;
    o['b'] = 6;
    o['t'] = 7;
    o['g'] = 9;

    for (int i = 0; i < n; i++) {
        string str;
        getline(cin, str);
        int sum = 0;
        for (char c: str) sum += o[tolower(c)];
        cout << sum << endl;
    }
}
