import java.util.Scanner;

public class Solution {
	static int w = 0;
	static int h = 0;

	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		w = in.nextInt();
		h = in.nextInt();
		Box[][] arr = new Box[w][h];

		if (in.hasNextLine()) {
			in.nextLine();
		}
		for (int y = 0; y < h; y++) {
			String s = in.nextLine();

			for (int x = 0; x < s.length(); x++) {
				char c = s.charAt(x);
				if (c != ' ') {
					Box b = new Box(x, y, c);
					arr[x][y] = b;
				}
			}
		}

		for (int i = 0; i < w; i++) {
			int y = 0;
			if (arr[i][y] != null) {
				Box b = arr[i][y];
				char startCh = b.c;
				while (y < h - 1) {
					b = goDown(arr, b);
					y = b.y;
				}
				char endCh = b.c;
				System.out.printf("%c%c\n", startCh, endCh);
			}
		}
	}

	private static Box goDown(Box[][] arr, Box b) {
		Box lowerL = null;
		Box below = null;
		Box lowerR = null;

		below = arr[b.x][b.y + 1];

		if (b.x < w - 3) {
			lowerR = arr[b.x + 1][b.y + 1];
		}

		if (b.x >= 3) {
			lowerL = arr[b.x - 1][b.y + 1];
		}

		if (lowerL == null && lowerR == null) {
			return below;
		}

		if (lowerL == null && lowerR != null && lowerR.c == '-') {
			return arr[b.x + 3][b.y + 1];
		}

		if (lowerL != null && lowerR == null && lowerL.c == '-') {
			return arr[b.x - 3][b.y + 1];
		}

		return below;
	}
}

class Box {
	int x, y;
	char c;

	public Box(int x, int y, char c) {
		this.x = x;
		this.y = y;
		this.c = c;
	}
}
