data.solution:
name_r, pts = input().split(',')
n = int(input())
points = [int(input()) for i in range(n)]

all_rank = ["Wood 2","Wood 1","Bronze 3","Bronze 2","Bronze 1","Silver 3","Silver 2","Silver 1","Gold 3","Gold 2","Gold 1","Legend"]
new_current_rank = all_rank.index(name_r)
current_pts = int(pts)

for p in points:
    current_pts += p
    if not all_rank[new_current_rank] == "Legend":
        if current_pts >= 100:
            new_current_rank += 1
            current_pts -= 100
        elif current_pts < 0:
            if all_rank[new_current_rank] != "Wood 2":
                new_current_rank -= 1
                current_pts = 100 + current_pts
            else:
                current_pts = 0    
    else:
        if current_pts < 0:
            new_current_rank -= 1
            current_pts = 100 + current_pts

print(f"{all_rank[new_current_rank]},{current_pts}")
