a=list(input())
b=list(input())
w=x=i=0
if len(a)!=len(b):
    print("ERROR")
else:
    while i<len(a):
        if a[i]==b[i]:
            w+=1
            a.pop(i)
            b.pop(i)
        else:
            i+=1
    for i in a:
        if i in b:
            b.remove(i)
            x+=1
    if w==x==0:
        print("NOTHING")
    else:
        print(w*"+"+x*"-")
