{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());

        int sum = 0;

        for(int i=0;i<N;i++){
            sum += Console.ReadLine().Split(' ').Select(int.Parse).Count(_=>_==i);
        }


        Console.WriteLine(sum);
    }
