import java.util.*;
import java.io.*;
import java.math.*;
import java.awt.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int rows = in.nextInt();
        int cols = in.nextInt();

        int[][] playground = new int[rows][cols];

        if (in.hasNextLine()) {
            in.nextLine();
        }
        for (int i = 0; i < rows; i++) {
            String line = in.nextLine();
            for(int j=0; j<line.length(); j++) {
                playground[i][j] = Character.digit(line.charAt(j), 10);
            }
        }




        Point startPosition = new Point(0, 0);
        Point exitPosition = new Point(cols-1, rows-1);

        int nbOfPaths = getNumberOfPaths(playground, startPosition, exitPosition);

        System.out.println(nbOfPaths);
    }

    public static int getNumberOfPaths(int[][] playground, Point myPosition, Point exitPosition){

        if(myPosition.x == exitPosition.x && myPosition.y == exitPosition.y) {
            return 1;
        }

        if(myPosition.y == playground.length || myPosition.x == playground[myPosition.y].length || playground[myPosition.y][myPosition.x]== 1) {
            return 0;
        }

        Point rightPosition = myPosition;
        Point bottomPosition = myPosition;

        rightPosition = new Point(myPosition.x+1, myPosition.y);
        bottomPosition = new Point(myPosition.x, myPosition.y+1);
        return (getNumberOfPaths(playground, bottomPosition, exitPosition) + getNumberOfPaths(playground, rightPosition, exitPosition));
    }
}
