using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        int W = int.Parse(Console.ReadLine());
        int H = int.Parse(Console.ReadLine());
        int[][] map = new int[H][];

        for (int i = 0; i < H; i++)
        {
            string LINE = Console.ReadLine();
            var charLine = LINE.Split(' ');
            int[] intLine = new int[W];
            for (int j = 0; j < W; j++)
            {
                intLine[j] = (charLine[j] == "0") ? 0 : 1;
            }
            map[i] = intLine;
        }

        // Write an action using Console.WriteLine()
        // To debug: Console.Error.WriteLine("Debug messages...");

        var result = FindTreasure(map, W, H);
        Console.WriteLine(result["x"] + " " + result["y"]);
    }

    static Dictionary<string, int> FindTreasure(int[][] map, int w, int h)
    {            
        for (int y = 0; y < h; y++)
        {
            for (int x = 0; x < w; x++)
            {
                if (map[y][x] == 0 &&
                    IsObsticle(map, w, h, x-1, y-1) &&
                    IsObsticle(map, w, h, x, y-1) &&
                    IsObsticle(map, w, h, x+1,y-1) &&
                    IsObsticle(map, w, h, x-1, y) &&
                    IsObsticle(map, w, h, x+1, y) &&
                    IsObsticle(map, w, h, x-1, y+1) &&
                    IsObsticle(map, w, h, x, y+1) &&
                    IsObsticle(map, w, h, x+1, y+1))
                    {
                        return new Dictionary<string, int>
                        {
                            {"x", x},
                            {"y", y}
                        };
                    }
            }
        }

        return null;
    }

    static bool IsObsticle(int[][] map, int w, int h, int x, int y)
    {
        if (x < 0 || x == w)
        {
            return true;
        }
        else if (y < 0 || y == h)
        {
            return true;
        }
        else
        {
            return map[y][x] == 1 ? true : false;
        }
    }
}
