using System;

class Solution
{
    static void Main(string[] args)
    {
        var inputs = Console.ReadLine().Split(' ');
        string D = inputs[0];
        string X = inputs[1];
        string T = inputs[2];
        string U = inputs[3];

        int salary = int.Parse(D);
        int price = int.Parse(X);
        int birthdayCount = int.Parse(T);
        int currentDay = int.Parse(U);

        int money = 0;

        for (int i = 0; i < birthdayCount; i++)
        {
            if (currentDay < 30)
                currentDay++;
            else
                currentDay = 1;

            if (currentDay == 15)
                money += salary;
        }
        if (money!=0){
            if (money >= price)
                Console.WriteLine("true");
            else
                Console.WriteLine("false");
        }
        else
            Console.WriteLine("ruined");

    }
}
