n=+readline()
var results=[]
while(n--){
    word=readline().replace(/[^aeiou]/gi,'').split('').sort(function (a, b) {
        if (a.toLowerCase()<b.toLowerCase()) { return -1 }
        if (a.toLowerCase()>b.toLowerCase()) { return 1 }
        if (a.toLowerCase()==b.toLowerCase()) {
            if (a==a.toLowerCase()) { return -1 }
            else { return 1 }
        }
    })
    results.push(word[0]||'NONE')
}
results.map(x=>print(x))
