const n = readline();

let duration = 0;

for (let i = 0; i < n; i++) {
    const line = readline();
    const arr = line.split(' ');
    let addedDuration = arr[1] / arr[0] * 60;
    let trafficFactor = arr[2] === 'LIGHT' ? 1 : arr[2] === 'HEAVY' ? 1.25 : 1;
    duration += addedDuration * trafficFactor;
}

console.log(Math.ceil(duration));
