ruleset = input()
rainbow = input()

illegal_chars = 0
i = 0

for c in rainbow:
    if c in "QWERTYUIOPASDFGHJKLZXCVBNM":
        if len(ruleset) <= i or c != ruleset[i]:
            illegal_chars += 1
        else:
            i += 1

if illegal_chars == 0 and len(ruleset) == i:
    print("Rainbow accepted! 0")
else:
    print(f"Rainbow denied! {illegal_chars}")
