using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());
        double lenght = 0;
        double finalLenght = 0;
        int lastX = 0;
        int lastY = 0;
        int startX = 0;
        int startY = 0;
        for (int i = 0; i < n; i++)
        {
            string[] inputs = Console.ReadLine().Split(' ');
            int x = int.Parse(inputs[0]);
            int y = int.Parse(inputs[1]);
            if (i == 0)
            {
                startX = x;
                startY = y;
            }
            else
            {
                lenght = Math.Sqrt((x-lastX)*(x-lastX)+(y-lastY)*(y-lastY));
            }
            finalLenght = finalLenght + lenght;
            lastX = x;
            lastY = y;
            if (i == n-1)
            finalLenght = finalLenght + Math.Sqrt((x-startX)*(x-startX)+(y-startY)*(y-startY));
        }
        Console.WriteLine(Math.Round(finalLenght, 3));
    }
}
