#include <bits/stdc++.h>
using namespace std;

const int MX = 1e5 + 5;
bool ans[MX];

int main(){
	for(int e = 1; e <= 100000; e ++){
		for(int d = e; d <= 100000; d += e){
			int x = d / e;
			if(x % e != 0) continue;
			int c = x / e;
			int b = x + e;
			int a = x - e;
			if(a <= 0 || b <= 0 || c <= 0 || d <= 0) continue;
			int g = a + b + c + d;
			if(g <= 0 || g > 100000) continue;
			ans[g] = 1;
		}
	}

	int tc; cin >> tc;
	for(int i = 1; i <= tc; i ++){
		int x; cin >> x;
		if(ans[x]) cout << "YES\n";
		else cout << "NO\n";
	}

	return 0;
}
