 ******** You can do this with REGEX :D *********
 ************************************************
 **/

var N = parseInt(readline());
// object to store EMOJI as key and CODE as value
P={};
for (var i = 0; i < N; i++) {
    var inputs = readline().split(' ');
    var EMOJI = inputs[0];
    var CODE = inputs[1];
    P[EMOJI] = CODE;
}
var S = readline();
T=S.split("");

beginEmoji = false;
E=[];
//the result string after replacing
result='';
for(i=0;i<T.length;i++){
    //take the current and its next
    char = T[i];
    next = T[i+1];
    //in our emojis they start with ":" and "-"
    if( beginEmoji==false && (char==':'||char=='-') ){
        //bool indicate state of the current emoji
        beginEmoji=true;
        //array to store emoji to prevent duplicate chars
        E=[char];
    } else {
        if(beginEmoji==true){
            //always check the previous char in emoji (no duplicate)
            if(E[E.length-1]!=char)
                E.push(char);
            //the end of the current emoji
            if((char==')'||char=='D'||char=='-'||char=='*')&&(next!=')'&&next!='D'&&next!='-'&&next!='*')){
                beginEmoji=false
                EMOJI = E.join("");
                result += P[EMOJI];
            }
        } else result += char;
    }
}

print(result);
