data.solution:
n = input()

winner = []
grid = []

for i in range(n):
    row = raw_input()
    # check rows for match
    if len(set(row)) == 1:
        winner.append(row[0])
    grid.append(list(row))

# two diagonals
diagonal1 = "" # \
diagonal2 = "" # /
for x in range(n):
    # check columns for match
    column = ""
    for y in range(n):
        column += grid[y][x]
        # second diagonal has x+y = n-1
        if x + y == n-1:
            diagonal2 += grid[x][y]

    if len(set(column)) == 1:
        winner.append(column[0])

    # first diagonal has x == y
    diagonal1 += grid[x][x]

if len(set(diagonal1)) == 1:
    winner.append(diagonal1[0])
if len(set(diagonal2)) == 1:
    winner.append(diagonal2[0])

if len(set(winner)) > 1 or len(set(winner)) == 0:
    print "TIE"
else:
    print winner[0] + " WINS"
