 const n = parseInt(readline());
 let bestName = "";
 let bestValue = 0;
 for (let i = 0; i < n; i++) {
     var inputs = readline().split(' ');
     const name = inputs[0];
     const quantity = parseInt(inputs[1]);
     const price = parseInt(inputs[2]);
     if(quantity/price > bestValue) {
         bestValue = quantity/price;
         bestName = name;
     }
 }

