sw,sh=map(int,raw_input().split())
shape=[]
for i in range(sh):
    shape+=[raw_input()]
fw,fh=map(int,raw_input().split())
field=[]
for i in range(fh):
    field+=[raw_input()]

for y in range(sh-1,fh):
    fieldY=fh-1-y
    for x in range(0,fw-sw+1):
        # does shape fit?
        fit=True
        for row in range(sh):
            for col in range(sw):
                if field[fieldY+row][x+col]==shape[row][col]=='*':
                    fit=False
                    break
            if not fit:
                break
        if not fit:
            continue
        nbLines=0
        for row in range(sh):
            line=field[fieldY+row][:x]
            for col in range(sw):
                if field[fieldY+row][x+col]=='*' or shape[row][col]=='*':
                    line+='*'
                else:
                    line+='.'
            line+=field[fieldY+row][x+sw:]
            if line=='*'*fw:
                nbLines+=1
        if nbLines>0:
            print x,y
            print nbLines
            exit(0)
