    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String S = in.nextLine();
        String R = in.nextLine();
        String[] array = S.split(R);
        for(int i = 0; i < array.length; i++){
            String output = array[i];
            if(i!=0)
