use strict;
use warnings;

sub minify ($) {
    return lc join'', split ' ', shift;
}

my $N = <STDIN>;
my %cityNames = ();
for (0..$N-1) {
    my $cityName = <STDIN>;
    $cityNames{minify($cityName)} = $cityName;
}

my $M = <STDIN>;
for (0..$M-1) {
    my $uglyCityName = <STDIN>;
    print "$cityNames{minify($uglyCityName)}";
}
