class Solution
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());
        string leading = "";
        int leadingp = 0;
        int leadingDiff = 0;
        int leadingGoals = 0;
        for (int i = 0; i < n; i++)
        {
            string[] inputs = Console.ReadLine().Split(' ');
            string s = inputs[0];
            int p = int.Parse(inputs[1]);
            int x = int.Parse(inputs[2]);
            int y = int.Parse(inputs[3]);
            if (leadingp < p || (leadingp == p && leadingDiff < (x - y)) || (leadingp == p && leadingDiff == (x - y) && x > leadingGoals))
            {
                leading = s;
                leadingp = p;
                leadingDiff = x - y;
                leadingGoals = x;
            }
        }
        Console.WriteLine(leading);
    }
}
