#include<bits/stdc++.h>
using namespace std;
int main() {
    bool answer=true;
    int n;
    cin>>n;cin.ignore();
    int b[]={0,0,0};
    for(int i=0;i<n;i++){
        int x;
        cin>>x;cin.ignore();
        if(x==5) b[0]++;
        else if(x==10) b[1]++,b[0]--;
        else if(b[1]) b[1]--,b[0]--,b[2]++;
        else b[0]-=3,b[2]++;
        if(b[0]<0){
            answer=false;
            break;
        }
    }
    if(answer){
        cout<<b[0]<<' '<<b[1]<<' '<<b[2]<<endl;
    }else cout<<"Not enough change!"<<endl;
}
