using System;
using System.Collections.Generic;

class Solution
{
    const string VOWELS = "aeiou";

    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());

        var data = new Dictionary<string, string>();

        for (int i = 0; i < n; i++)
        {
            string word = Console.ReadLine();
            data.Add(TranslateToSnakeWord(word), word);
        }

        string transmission = Console.ReadLine();

        var translatedWord = "";
        var currentSnakeWord = "";
        for(int i = 0; i < transmission.Length; i++)
        {
            var letter = transmission[i];
            if(Char.IsLetter(letter))
            {
                currentSnakeWord += letter;
            }
            else
            {
                if(currentSnakeWord != "")
                {
                    translatedWord = data[currentSnakeWord.ToLower()];
                    for(int j = 0; j < currentSnakeWord.Length; j++)
                    {
                        if(Char.IsUpper(currentSnakeWord[j]))
                        {
                            Console.Write(Char.ToUpper(translatedWord[j]));
                        }
                        else
                        {
                            Console.Write(translatedWord[j]);
                        }
                    }
                }
                Console.Write(letter);
                currentSnakeWord = "";
            }
        }

        if(currentSnakeWord != "")
        {
            translatedWord = data[currentSnakeWord.ToLower()];
            for(int j = 0; j < currentSnakeWord.Length; j++)
            {
                if(Char.IsUpper(currentSnakeWord[j]))
                {
                    Console.Write(Char.ToUpper(translatedWord[j]));
                }
                else
                {
                    Console.Write(translatedWord[j]);
                }
            }
        }
    }

    private static string TranslateToSnakeWord(string word)
    {
        var translation = "";
        foreach(var letter in word)
        {
            if(VOWELS.IndexOf(letter) != -1)
            {
                translation += "z";
            }
            else
            {
                translation += "s";
            }
        }
        return translation;
    }
}
