import java.util.*;

public class Solution {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);

		int num = -1;
		String resp;
		List<Round> list = new ArrayList<>();

		int n = in.nextInt();
		if (in.hasNext()) {
			in.nextLine();
		}

		for (int i = 0; i < n; i++) {
			String str = in.nextLine().trim();
			String[] ss = str.split("\\s");
			num = Integer.parseInt(ss[0]);
			resp = ss[2];
			Round round = new Round(num, resp);
			list.add(round);
		}
		process(list);
	}

	private static void process(List<Round> list) {
		boolean arr[] = new boolean[101];
		Arrays.fill(arr, 1, arr.length, true);

		for (int j = 0; j < list.size(); j++) {
			Round r = list.get(j);

			if (r.isTooLow()) {
				Arrays.fill(arr, 0, r.num + 1, false);
				boolean result = false;
				for (int i = r.num + 1; i < arr.length; i++) {
					if (arr[i]) {
						result = true;
						break;
					}
				}
				if (!result) {
					System.out.println("Alice cheated in round " + (j + 1));
					return;
				}
			}

			else if (r.isTooHigh()) {
				Arrays.fill(arr, r.num, arr.length, false);
				boolean result = false;
				for (int i = r.num - 1; i >= 0; i--) {
					if (arr[i]) {
						result = true;
						break;
					}
				}
				if (!result) {
					System.out.println("Alice cheated in round " + (j + 1));
					return;
				}
			}

			else {
				if (arr[r.num]) {
					System.out.println("No evidence of cheating");
					return;
				} else {
					System.out.println("Alice cheated in round " + (j + 1));
					return;
				}
			}
		} // for
		System.out.println("No evidence of cheating");
	}
}

class Round {
	int num;
	Respond respond;

	public enum Respond {
		TOOHIGH, TOOLOW, RIGHTON
	}

	public Round(int num, String str) {
		this.num = num;
		if (str.equals("low")) {
			this.respond = Respond.TOOLOW;
		} else if (str.equals("high")) {
			this.respond = Respond.TOOHIGH;
		} else {
			this.respond = Respond.RIGHTON;
		}
	}

	public boolean isTooLow() {
		return respond == Respond.TOOLOW ? true : false;
	}

	public boolean isTooHigh() {
		return respond == Respond.TOOHIGH ? true : false;
	}

	public boolean isRightOn() {
		return respond == Respond.RIGHTON ? true : false;
	}
}
