import java.util.*;

class Solution {

	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		int m = in.nextInt();
		int c = in.nextInt();

		boolean[] isOn = new boolean[n + 1];
		int[] consumes = new int[n + 1];

		for (int i = 1; i <= n; i++) {
			consumes[i] = in.nextInt();
		}

		int total = 0;
		int max = 0;
		for (int i = 0; i < m; i++) {
			int id = in.nextInt();
			if (isOn[id]) {
				isOn[id] = false;
				total -= consumes[id];
			} else {
				isOn[id] = true;
				total += consumes[id];
				if (total > max) {
					max = total;
				}
			}
		}
		in.close();

		if (max > c) {
			System.out.println("Fuse was blown.");
		} else {
			System.out.printf("Fuse was not blown.\nMaximal consumed current was %d A.\n", max);
		}
	}
}
