const typedKeys = readline();
let cursor = 0;
let text = '';

for (let key of typedKeys) {

    if (key == '<') {
        if (cursor > 0) {
            cursor--;
        }
    }
    else if (key == '>') {
        if (cursor < text.length) {
            cursor++;
        }
    }
    else if (key == '-') {
        if (cursor > 0) {
            text = text.slice(0, cursor-1) + text.slice(cursor)
            cursor--;
        }
    }
    else {
        text = text.slice(0, cursor) + key + text.slice(cursor);
        cursor++;
    }

}

