import java.util.Scanner;

public class Solution {

	public static void main(String[] args) {

		Scanner sc = new Scanner(System.in);
		int[] x = new int[4];
		int[] y = new int[4];
		for (int i=0; i<4; i++){
			x[i] = sc.nextInt();
			y[i] = sc.nextInt();
		}
		int twiceArea = 0;
		for (int i=0; i<4; i++)
			twiceArea += x[i]*y[(i+1)%4] - x[i]*y[(i-1)>=0?(i-1):3];
		System.out.println(Math.abs(twiceArea)/2.0);
	}

}
