using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        int SIZE = int.Parse(Console.ReadLine());
        int numCompetitors = int.Parse(Console.ReadLine());
        var competitors = Enumerable.Range(0, numCompetitors)
            .Select((s, i) => new Competitor(Console.ReadLine(), i))
            .ToList();
        int numThrows = int.Parse(Console.ReadLine());
        for (int i = 0; i < numThrows; i++)
        {
            var throwData = Console.ReadLine().Split(' ');
            var competitor = competitors.First(s => s.Name == throwData[0]);
            int x = int.Parse(throwData[1]);
            int y = int.Parse(throwData[2]);
            int points = GetPoints(x, y, SIZE);
            competitor.Score += points;
        }
        var answer = competitors
            .OrderByDescending(s => s.Score)
            .ThenBy(s => s.ListOrder);
        answer.ToList().ForEach(s => Console.WriteLine($"{s.Name} {s.Score}"));
    }

    static int GetPoints(int x, int y, int size)
    {
        double s2 = ((double) size) / 2;
        if (InDiamond(x, y, s2))
        {
            return 15;
        }
        if (InCircle(x, y, s2))
        {
            return 10;
        }
        if (InSquare(x, y, s2))
        {
            return 5;
        }
        return 0;
    }
    static bool InDiamond(int x, int y, double size)
    {
        int dist = Math.Abs(x) + Math.Abs(y);
        return dist <= size;
    }
    static bool InCircle(int x, int y, double radius)
    {
        x *= x;
        y *= y;
        radius *= radius;
        return x + y <= radius;
    }
    static bool InSquare(int x, int y, double size)
    {
        return x >= -size && x <= size && y >= -size && y <= size;
    }
}
public class Competitor
{
    public int ListOrder { get; set; }
    public int Score { get; set; } = 0;
    public string Name { get; }
    public Competitor(string name, int listOrder)
    {
        Name = name;
        ListOrder = listOrder;
    }
}
