use 5.020;
use warnings;
use Memoize;

sub solve {
  no warnings 'recursion';
  my ($h,$w) = @_;
  if ($h > $w) { return solve ($w,$h) }
  if ($h == 1) { return $w }
  if ($h == $w) { return 1 }
  my $min = $w*$h;
  for my $c (1 .. int($w/2)) {
    my $r = solve( $h, $c ) + solve ($h, ($w-$c));
    $min = $r if $r < $min;
  }
  for my $c (1 .. int($h/2)) {
    my $r = solve( $c, $w ) + solve (($h-$c), $w);
    $min = $r if $r < $min;
  }
  return $min;
}

memoize 'solve';

my ($h,$w) = <> =~ /\d+/g;
say solve ($h,$w);
