function rotateToLeft(tab){
    newtab = [];
    for(var i = 0; i < S; i++){
        newtab[i] = [];
        for(a = 0; a < S; a++){
            newtab[i][a] = tab[a][S-i-1];
        }
    }
    return (newtab);
}

function rotateToRight(tab){
    newtab = [];
    for(var i = 0; i < S; i++){
        newtab[i] = [];
        for(a = 0; a < S; a++){
            newtab[i][a] = tab[S-a-1][i];
        }
    }
    return (newtab);
}

function printTab(tab){
    for(i = 0; i < tab.length; i++){
        print(tab[i].join(" "));
    }
}

S = parseInt(readline());
O = readline().split(";");
tab = [];

for (let i = 0; i < S; i++) {
    tab[i] = readline().split(" ");
}

for (i = 0; i < O.length; i++){
    if (O[i] == "left"){
        tab = rotateToLeft(tab);   
    }
    else {
        tab = rotateToRight(tab);
    }
}
printTab(tab);
