import java.util.*;
public class Solution {
    public static void main(String[] args){
        Scanner scan = new Scanner(System.in);
        String s = scan.nextLine();
        String spaces = "                ";

        if (s.length() <= 16)
            System.out.println(spaces.substring(0,16-s.length()) + s);
        else {
            int index = 0;
            while (index + 16 <= s.length()) {
                System.out.println(s.substring(index, index+16));
                index++;
            }
        }
    }
}
