var notes = "C C# D D# E F F# G G# A A# B".split(' ');
var intervals = {
    "major": [0, 4, 7],
    "minor": [0, 3, 7],
    "diminished": [0, 3, 6],
    "augmented": [0, 4, 8],
};

var line = readline().split(' ');
root = notes.indexOf(line[0]);
print(intervals[line[1]].map(int => notes[(root + int) % 12]).join(' '));
