using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        string name = Console.ReadLine();
        string line1 = name + ":";
        name = name.ToUpper();
        string phrases = "altruistic acts, an award, a breakup, career change, completion of a big project, crime, disappointment, disaster, an epic experience, new love, forgiveness, illness, injury, incarceration, inspiration, investment gain, lifestyle change, a new friend, moments of wonder and clarity, a move, realizing something important, reconciliation, redemption, solving a problem, a transformative experience, a trip";
        var PhraseRay = phrases.Split(',');
        string line2 = "";
        string allLetters = "";
        for (int i = 65 ; i <=90 ; i++)
        {
            allLetters = allLetters + (char)i;
        }

        List<char> AlreadyUsed = new List<char>();
        int count = 0;

        foreach (char ch in name)
        {
            bool isNormalLetter = allLetters.Contains(ch);
            if (isNormalLetter && AlreadyUsed.Contains(ch) == false && count < 3 )
            {
                int whichLtr = allLetters.IndexOf(ch);
                line2 = line2 + PhraseRay[whichLtr].Trim().ToLower() + " and ";
                AlreadyUsed.Add(ch);
                count++;
            }
        }

        line2 = line2.Substring(0,line2.Length-5); // removes final (unneeded) " and "

        line2 = "There will be " + line2 + " in your future.";

        Console.WriteLine(line1);
        Console.WriteLine(line2);
    }
}
