using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        string nameAsGiven = Console.ReadLine();
        string name = nameAsGiven.ToUpper();

        string adjList = "Adaptable Adventurous Affectionate Courageous Creative Dependable Determined Diplomatic Giving Gregarious Hardworking Helpful Hilarious Honest Non-judgmental Observant Passionate Sensible Sensitive Sincere";
        string goodList = "Love, Forgiveness, Friendship, Inspiration, Epic Transformations, Wins";
        string badList = "Crime, Disappointment, Disasters, Illness, Injury, Investment Loss";

        var AdjList = adjList.Split(" ");
        var GoodList = goodList.Split(',');
        var BadList = badList.Split(',');

        string vows = "aeiouy".ToUpper();
        string allLetters = "";
        for (int i = 65 ; i <=90 ; i++)  {  allLetters = allLetters + (char)i;  }
        string cons = Filter(allLetters,vows);

        List<char> AlreadyUsed = new List<char>();

        var List_posInAlphabetOfTheirUniqueCONS = new List<int>();  
        var List_posInAlphabetOfTheirVOWS = new List<int>();  

        foreach (char ch in name) // for cons
        {
            bool isNormalLetter = allLetters.Contains(ch);
            if (isNormalLetter && AlreadyUsed.Contains(ch) == false)
            {
                if (cons.Contains(ch))
                {
                    int consIndex = cons.IndexOf(ch);
                    List_posInAlphabetOfTheirUniqueCONS.Add(consIndex);
                }
                AlreadyUsed.Add(ch);
            }
        }

        foreach (char ch in name) // for vows;
        {
            bool isNormalLetter = allLetters.Contains(ch);
            if (isNormalLetter)
            {
                if (vows.Contains(ch))
                {
                    int vowIndex = vows.IndexOf(ch);
                    List_posInAlphabetOfTheirVOWS.Add(vowIndex);
                }
            }
        }

        bool notEnoughCons = List_posInAlphabetOfTheirUniqueCONS.Count < 3;
        bool notEnoughtVows = List_posInAlphabetOfTheirVOWS.Count < 2;

        if (notEnoughCons || notEnoughtVows)
        {
            Console.WriteLine("Hello " + nameAsGiven + ".");
            return;
        }

        string str1 = AdjList[List_posInAlphabetOfTheirUniqueCONS[0]].ToLower();
        string str2 = AdjList[List_posInAlphabetOfTheirUniqueCONS[1]].ToLower();
        string str3 = AdjList[List_posInAlphabetOfTheirUniqueCONS[2]].ToLower();
        string str4 = GoodList[List_posInAlphabetOfTheirVOWS[0]].Trim().ToLower();
        string str5 = BadList[List_posInAlphabetOfTheirVOWS[1]].Trim().ToLower();

        StringBuilder sb1 = new StringBuilder();
        sb1.Append("It's so nice to meet you, my dear ");
        sb1.Append(str1 + " ");
        sb1.Append(nameAsGiven + ".");

        StringBuilder sb2= new StringBuilder();
        sb2.Append("I sense you are both ");
        sb2.Append(str2 + " and " + str3 + ".");

        StringBuilder sb3= new StringBuilder();
        sb3.Append("May our future together have much more ");
        sb3.Append(str4 + " than ");
        sb3.Append(str5 + ".");

        Console.WriteLine(sb1);
        Console.WriteLine(sb2);
        Console.WriteLine(sb3);
    }


    public static string Filter(string str, string removeThese)
    {
        foreach (char ch in removeThese)
        {
            str = str.Replace(ch+"","");
        }
        return str;
    }
}
