from math import acos,pi,sqrt

def dist(A,B):
    return (A[0]-B[0])**2+(A[1]-B[1])**2

n=int(input())
phrase="%s is a%s and a%s%s triangle."

for _ in range(n):
    triangle=input().split()
    fin=""
    points={triangle[i]:(int(triangle[i+1]),int(triangle[i+2])) for i in range(0,9,3)}
    nom="".join(triangle[::3])
    l01=dist(points[nom[0]],points[nom[1]])
    l12=dist(points[nom[1]],points[nom[2]])
    l20=dist(points[nom[2]],points[nom[0]])
    if l01==l12==l20:
        cote="n equilateral"
    elif len({l01,l12,l20})==2:
        cote="n isosceles in "
        if l01==l12:
            cote+=nom[1]
        elif l12==l20:
            cote+=nom[2]
        else:
            cote+=nom[0]
    else:
        cote=" scalene"
    if l01+l12==l20:
        angle=" right in "+nom[1]
    elif l12+l20==l01:
        angle=" right in "+nom[2]
    elif l20+l01==l12:
        angle=" right in "+nom[0]
    else:
        m0,m1,m2=sorted([l12,l20,l01])
        if m2>m0+m1:
            angle="n obtuse"
            fin=" in %s (%d°)"%(nom[[l12,l20,l01].index(m2)],int(round(180*acos((m1+m0-m2)/(2*sqrt(m1*m0)))/pi)))
        else:
            angle="n acute"
    print(phrase%(nom,cote,angle,fin))
